def check_task_status(task_ids: list[str] | str):
    """Check the status of an Earth Engine task."""
    if isinstance(task_ids, str):
        task_ids = [task_ids]
    status = ee.data.getTaskStatus(task_ids)
    for state in status:
        print(state)

